def getSubsetImage(image, imageFrom, imageTo):

    subsetImage = [[] for i in range(imageTo - (imageFrom -1))]
    i = imageFrom - 1
    j = 0
    while i < imageTo:
        subsetImage[j] = image[i]
        i += 1
        j += 1

    #subset = [subsets, subsetImage]

    return subsetImage

