def verticalKippen(image):
    adaptImage = [[[] for i in range(len(image[0]))] for i in range(len(image))]

    x = 0
    while x < len(image):
        y = 0
        yAdapt = len(image[x]) - 1
        while y < len(image[x]):
            adaptImage[x][yAdapt] = image[x][y]
            y += 1
            yAdapt -= 1
        x += 1

    return adaptImage


